import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {
  const [Htitle, setHtitle] = useState("");
  const [HsubTitle, setHsubTitle] = useState("");
  const [notes, setNotes] = useState([]);

  function onAdd(e) {
    notes.push({title : Htitle, content : HsubTitle});
    // console.log(notes);
    // e.preventDefault();
  }

  function handleChange(e) {
    setHtitle(e.target.value);
  }

  function handleChangeSub(e) {
    setHsubTitle(e.target.value);
  }

  return (
    <div>
      <Header />
      <CreateArea 
        Htitle={Htitle}
        HsubTitle={HsubTitle}
        Add={onAdd}
        handleChange={handleChange}
        handleChangeSub={handleChangeSub}
      />
      {notes.map(() => {
        return <Note key={1} title="Note title" content="Note content" />
      })}

      <Footer />
    </div>
  );
}

export default App;
